import React from 'react'
import {Link} from 'react-router-dom';
import './Page1.css';
import axios from 'axios';
import { postCompInfo } from '../../service/api.js';

const Page1 = (props) => {
  const [register, setRegister]=React.useState(false);
    function handleChange(event){
      props.setcname(event.target.value);
    }
    
  const [formData, setFormData] = React.useState({
    company_id: '',
     company_name: '',
    contact_no: '',
    email_id: '',
    location:''
  });

  
    function handleClick(){

    }
    function handleYes(){
      setRegister(true);
    
    }
    function handleNo(){
      setRegister(true);
    }
   
      const handleChangeF= (event)=>{
        if(event.target.name='compname'){
          handleChange(event);
        }
        setFormData({...formData, [event.target.name]: event.target.value});
        
      }
      const handleClickF=async()=>{
        await postCompInfo(formData);
       
        console.log("post req successful");
      }
    

  return (
    <>
      <div className='outerpg1'>
      <div>Is your company already registered?</div>
      <button onClick={handleYes}>Yes</button>
      <button onClick={handleNo}>No</button>
        {register?<div className='lowerpg1'>
        <input type="text" className="form-control" name='compname' onChange={handleChange} placeholder='Enter company name'/>
        <Link to='/select'><button className='btn btn-primary' onClick={handleClick}>Save</button></Link>
        </div>
        : <div className='lowerpg1'>
        
        <input type="text" className="form-control" name='compid' onChange={handleChangeF} placeholder='Enter company id'/>
        <input type="text" className="form-control" name='compname' onChange={handleChangeF} placeholder='Enter company name'/>
        <input type="text" className="form-control" name='compnum' onChange={handleChangeF} placeholder='Enter contact'/>
        <input type="text" className="form-control" name='compemail' onChange={handleChangeF} placeholder='Enter email id'/>
        <Link to='/select'><button className='btn btn-primary' onClick={handleClickF}>Save</button></Link>
        </div> }

      </div>
    </>
  )
}

export default Page1;
